import React from "react";
import { Button, Card, CardBody, CardText } from "reactstrap";
import YourComponent from "./html-text";
import { Link } from "react-router-dom";

export function Post({
  post = {
    title: "This is default post title",
    content: "this is default post content",
  },
}) {
  const truncatedContent = post.content.substring(0, 1000);
  return (
    
    <Card className="border-0 shadow-sm mt-3">
      <CardBody>
        <h1>{post.title}</h1>
        <CardText>
          <YourComponent htmlContent={truncatedContent} />
        </CardText>
        
        <div>
          <Link className="btn btn-secondary border-2" to={'/post/'+post.postId}>Read More..</Link>
          
        </div>
      </CardBody>
    </Card>
  );
}
