import React, { useContext, useEffect, useState } from "react";
import Base from "../../components/Base";
import userContext from '../../context/userContext'
import { useParams } from "react-router-dom";
import { getUser } from "../../Services/user-service";
import { Card, CardBody, Col, Row } from "reactstrap";
function ProfileInfo() {
  const object=useContext(userContext)
  const [user,setUser] = useState(null);
  const {userId} = useParams()
  useEffect(()=>{
    getUser(userId).then(data=>{
      console.log(data)
      setUser({...data})
    })
  },[])
  const userView =()=>{
    return (
      <Row>
        <Col md={{size:8, offset:2}}>
          <Card>
            <CardBody>
              <h3 className='text-uppercase'>User Information</h3>
              <Container className='text-center'>
                <img style={{maxWidth}} src="https://www.google.com/imgres?imgurl=https%3A%2F%2Fcdn-icons-png.flaticon.com%2F512%2F3177%2F3177440.png&tbnid=8QiS3pbwiwJ_JM&vet=10CFAQMyiLAWoXChMIgKW_3aaRhAMVAAAAAB0AAAAAEAM..i&imgrefurl=https%3A%2F%2Fwww.flaticon.com%2Ffree-icon%2Fuser_3177440&docid=OwrTpL_tGVFPEM&w=512&h=512&q=user%20image&ved=0CFAQMyiLAWoXChMIgKW_3aaRhAMVAAAAAB0AAAAAEAM" alt="User profile picture" className="img-fluid"></img>
              </Container>
            </CardBody>
          </Card>

        </Col>
      </Row>
    )
  }
  return (
    <Base>
      {userView()}
    </Base>
  );
}
export default ProfileInfo;
