import { useEffect, useRef, useState } from "react";
import { Button, Card, CardBody, Container, Form, Input, Label } from "reactstrap";
import loadAllCategories from "../Services/category-service";
import JoditEditor from 'jodit-react';
import { json } from "react-router-dom";
import { createPost as doCreatePost} from "../Services/post-service";
const AddPost = () => {
    const editor = useRef(null)

    const [content,setContent]=useState('')

    const [category,setCategory]=useState([])

    const [post,setPost]=useState({
        title:'',
        content:'',
        categoryId:''

    })
    const fieldChaged=(event)=>{
        //console.log(event)
        setPost({...post,[event.target.name]: event.target.value})
    }
    const contentFieldChanged=(data)=>{
        setPost({...post,'content':data})
    }
    useEffect(
        ()=>{
            loadAllCategories().then((data)=>{
                console.log(data)
                setCategory(data)
            }).catch(error=>{
                console.log(error)
            })
        },[]
    )
    //create post function
    const createPost = (event)=>{
        event.preventDefault();
        console.log(post)
        console.log("form submitted")
        if(post.title.trim()===''){
            alert("Post title is required !!")
            return;
        }
        if(post.content.trim()===''){
            alert("Post content is required !!")
            return;
        }
        if(post.categoryId===''){
            alert("select some category !!")
            return;
        }
        //submit the form on server
        createPost(post).then(data=>{
            alert("Post Created")
            console.log(post)
        }).catch((error)=>{
            alert("error")
            console.log(error)
        })

    }
  return (
    <div className="wrapper">
      {/* <h1>We are going to develop add post </h1> */}
      <Card className="shadow-lg border-3 mt-2">
        <CardBody>
            
          <h3> Whats on you Mind</h3>
          <Form onSubmit={createPost}>
            <div className="my-3">
              <Label for="title">Post Title</Label>
              <Input
                type="text"
                id="title"
                placeholder="Enter here"
                className="rounded-0"
                name="title"
                onChange={fieldChaged}
              ></Input>
            </div>
            <div className="my-3">
              <Label for="content">Post Content</Label>
              {/* <Input
                type="textarea"
                id="content"
                placeholder="Enter here"
                className="rounded-0"
                style={{ height: "300px" }}
              ></Input> */}
              <JoditEditor 
              ref={editor}
              value={content}
              onChange={contentFieldChanged}
              >

              </JoditEditor>
            </div>
            <div className="my-3">
              <Label for="category">Post Category</Label>
              <Input
                type="select"
                id="category"
                placeholder="Enter here"
                className="rounded-0"
                name="categoryId"
                onChange={fieldChaged}
                defaultValue={0}
              >
                <option disabled value={0}>--Select category--</option>
                {
                    category.map((category)=>(
                        <option value={category.id} key={category.id}>
                            {category.title}
                        </option>
                    ))

                    
                }
              </Input>
            </div>
            <Container className="text-center">
                <Button type="submit" color="primary">
                    Create Post
                </Button>
                <Button className="ms-2" color="danger">
                    Reset Post
                </Button>
            </Container>
          </Form>
          
        </CardBody>
      </Card>
    </div>
  );
};
export default AddPost;
