import React from 'react'
import { Card, CardBody, CardText } from "reactstrap";

export function Post({title="This is default post title",content="this is default post content"}){
    return (
        <Card className='border-0 shadow-sm'>
            <CardBody>
                <h1>{title}</h1>
                <CardText>
                    <p>{content}</p>
                </CardText>
            </CardBody>
        </Card>
    )
}
